import React from 'react';
import { Zoom } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css'
import slide_2 from './images/slide_2.jpg';
import slide_3 from './images/slide_3.jpg';
import slide_4 from './images/slide_4.jpg';
import Rabbi from './images/rabbi.jpg';
import { withStyles,Card,Typography,Accordion,
  AccordionSummary ,AccordionDetails,Paper  } from '@material-ui/core';
  import Divider from '@material-ui/core/Divider';
  import { ReactComponent as YourSvg } from './svg/torah.svg';
 
const Slideshow = () => {
  const images = [
    slide_2,
    slide_3,
    slide_4,
    slide_2,
    slide_3
  ];
  
  const zoomInProperties = {
    indicators: true,
    scale: 1.4,
   
  }
  return (
  <div>
    <div style={{
      paddingTop:80, 
       Width:window.innerWidth,
       minHeight:(window.innerHeight>900)?window.innerHeight-200:window.innerHeight,
      // backgroundImage: `url(  ${ images[0] }  )` ,
     // marginTop:theme.spacing(),
     
      width:'auto',
      zIndex:-1,
      //height:window.innerhight,
     
      background: `linear-gradient(#0f0c29, #24243e)`
      //   //backgroundPosition: 'center',
      //    backgroundSize:"100% 95%",
      //   backgroundRepeat: 'no-repeat',
      }}>
      <Typography style={{
        direction:"rtl",
        //paddingTop:30,
        //mxwidth:500,
        paddingRight:window.innerWidth*0.02,
        color:"#ff9933",
      // color:"#fff"
        }} 
        
        variant="h5"
        >
        בית הכנסת אהבת ישראל
      </Typography>
      {/* <Divider style={{paddingTop:5,width: window.innerWidth*0.5}}/> */}
      <div style={{paddingTop:50,display:"flex", justifyContent: 'flex-end',flexDirection:"row"}}>
      <img style={{height:window.innerHidth*0.22,width:window.innerWidth*0.22,paddingRight:window.innerWidth*0.09,height:400}} src={Rabbi}></img>

        <Zoom {...zoomInProperties} transitionDuration={500} style={{width: window.innerWidth,maxWidth:1000,paddingRight:(window.innerHeight>900)?window.innerWidth*0.02:0}}>
          {images.map((each, index) => 
          {return (
            <div key={index} style={{width: '100%'}}>
              <img style={{ objectFit: "cover", width: '100%',height:400 }} src={each}/>
            </div>
          )})}
        </Zoom>

      </div>

      
      <Typography style={{direction:"rtl",paddingTop:30,paddingRight:window.innerWidth*0.02,}} color="primary" variant="h5">
        רקע כללי
      </Typography>

      <Typography style={{direction:"rtl",paddingRight:window.innerWidth*0.02,paddingTop:10}} color="primary" variant="h5">
        בית הכנסת "אהבת ישראל"
      </Typography>
      <div style={{display:"flex", justifyContent: 'flex-end'}}>
        <Typography style={{ paddingRight:window.innerWidth*0.02,paddingTop:10 ,color:"#fff", direction:"rtl",maxWidth:1000}}  variant="h5">
        במזרח גוש עציון למרגלות ההרדיון, על ספר מדבר יהודה, נמצא הישוב הפסטורלי נוקדים ובו בית הכנסת אהבת ישראל. 
        הישוב הוקם בשנת 1982 ומונה כיום כ-300 משפחות המנהלות אורח חיים קהילתי מגוון 
        המעניק תחושת אחדות הבנה וכבוד הדדי בין אופיין הייחודי של המשפחות.
        </Typography>
      </div>


    </div>

    <Paper style={{hight:900,display:"flex",flexDirection:"column"}}>
   

    <div style={{display:"flex", justifyContent: 'flex-end',flexDirection:"row"}}>

        <YourSvg style={{height:window.innerHidth*0.22,width:window.innerWidth*0.22,paddingTop:50,paddingRight:window.innerWidth*0.09}}></YourSvg>

        <div style={{display:"flex",flexDirection:"column",paddingTop:50}}>

            <Typography style={{ paddingRight:window.innerWidth*0.02,paddingTop:10 ,direction:"rtl",maxWidth:1000}}  variant="h5">
