import React,{Component}from 'react';
import { Zoom } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css'
import video_1 from '../video/video_1.mp4';
import video_2 from '../video/video_2.mp4';
import video_3 from '../video/video_3.mp4';
import video_4 from '../video/video_4.mp4';
import video_5 from '../video/video_5.mp4';
//import video_6 from '../video/video_6.mp4';
import slide_2 from '../images/slide_2.jpg';
import slide_3 from '../images/slide_3.jpg';
import slide_4 from '../images/slide_4.jpg';
import slide_5 from '../images/slide_5.jpg';
import slide_6 from '../images/slide_6.jpg';
import slide_7 from '../images/slide_7.jpg';
import slide_8 from '../images/slide_8.jpg';
import slide_9 from '../images/slide_9.jpg';
import slide_10 from '../images/slide_10.jpg';
import slide_11 from '../images/slide_11.jpg';
import slide_12 from '../images/slide_12.jpg';
import slide_13 from '../images/slide_13.jpeg';
import slide_14 from '../images/slide_14.jpeg';
import slide_15 from '../images/slide_15.jpeg';
import slide_16 from '../images/slide_16.jpeg';
import slide_17 from '../images/slide_17.jpeg';
import slide_18 from '../images/slide_18.jpeg';
import slide_19 from '../images/slide_19.jpeg';
import slide_20 from '../images/slide_20.jpeg';
import slide_21 from '../images/slide_21.jpeg';
import slide_22 from '../images/slide_22.jpeg';
import slide_23 from '../images/slide_23.jpeg';
import slide_24 from '../images/slide_24.jpeg';
import slide_25 from '../images/slide_25.jpeg';
//import stars from './images/stars.jpg';
import { withStyles,Typography,CardContent,
  AccordionSummary ,Card,Paper  } from '@material-ui/core';
  import Divider from '@material-ui/core/Divider';
  import { ReactComponent as YourSvg } from '../svg/torah.svg';
  import { ReactComponent as Sedor } from '../svg/sedor.svg';
  import styled, { keyframes } from 'styled-components'
  import ReactDOM from "react-dom";
 









export default class main extends Component {
  constructor(props) {
    super(props);
    this.myRef = React.createRef();
    this.state={
      index: 0,
      flag:true,
      width:window.innerWidth,
      height:window.innerHeight,
      srcNow:video_1,
      src: [
        video_1,
        video_2,
        video_4,
        video_5,
        video_3,
        
      
      
       
      ]
    }
  }
  componentWillMount(){
    if(this.state.flag===true){ 
      let rand=(Math.floor(Math.random() * (this.state.src.length-1)));
      (this.setState({srcNow:this.state.src[rand]}));
      this.state.index=rand;
    }
   else{(this.state.flag=false)}
  
 
  }
  componentDidMount() {
    let video = this.myRef.current;
   
    video.addEventListener("ended", e => {
      ///e.preventDefault();
      if (this.state.index === this.state.src.length-1) {
        this.setState({
          index:-1,
         
        });
      }
      if (this.state.index < this.state.src.length) {
        let nextIndex = this.state.index + 1;
        video.src=this.state.src[nextIndex];
        this.setState({
          index: nextIndex,
          srcNow: this.state.src[nextIndex]
        });

      }
     
    });
    this.forceUpdate()
    video.play();
    
  }
  componentDidUpdate(prevProps, prevState) {
    let video = this.myRef.current;
    video.play();
    
  }
  
 
  render() {
   console.log(this.state.srcNow,"insex")
    const images = [
      slide_23,
      slide_22,
      slide_14, 
      slide_15, 
      slide_16, 
      slide_17,
      slide_18, 
      slide_19, 
      slide_25,
      slide_21,
      slide_20, 
       
      slide_24, 
      slide_13, 
      slide_11, 
      slide_5,
      slide_6,
      slide_7,
      //slide_8,
      //slide_9, 
      slide_10, 
      //slide_2,
      slide_12, 
      // slide_3,
      // slide_4,
      
     ];
 
  
  const zoomInProperties = {
    indicators: true,
    scale: 1.4,
   
  }


return (
<div>
<video
  ref={this.myRef}
 
  muted
  style={{position:"absolute",height:window.innerHeight,width:window.innerWidth, zIndex:-1,objectFit:"cover"}}
  >
    <source src={this.state.srcNow} type="video/mp4"></source>
  </video>


