import React, { Component } from 'react'
import PeopleRoundedIcon from '@material-ui/icons/PeopleRounded';
import { 
    withStyles,
    Card,
    Typography,
    Paper,
    CardActionArea,
    CardActions,
    CardContent,
    CardMedia,
    Button
} from '@material-ui/core';
import { forwardRef } from 'react';
import MaterialTable, { MTableToolbar } from 'material-table';
import AddBox from '@material-ui/icons/AddBox';
import ArrowDownward from '@material-ui/icons/ArrowDownward';
import Check from '@material-ui/icons/Check';
import ChevronLeft from '@material-ui/icons/ChevronLeft';
import ChevronRight from '@material-ui/icons/ChevronRight';
import Clear from '@material-ui/icons/Clear';
import DeleteOutline from '@material-ui/icons/DeleteOutline';
import Edit from '@material-ui/icons/Edit';
import FilterList from '@material-ui/icons/FilterList';
import FirstPage from '@material-ui/icons/FirstPage';
import LastPage from '@material-ui/icons/LastPage';
import Remove from '@material-ui/icons/Remove';
import SaveAlt from '@material-ui/icons/SaveAlt';
import Search from '@material-ui/icons/Search';
import ViewColumn from '@material-ui/icons/ViewColumn';



const tableIcons = {
    Add: forwardRef((props, ref) => <AddBox {...props} ref={ref} />),
    Check: forwardRef((props, ref) => <Check {...props} ref={ref} />),
    Clear: forwardRef((props, ref) => <Clear {...props} ref={ref} />),
    Delete: forwardRef((props, ref) => <DeleteOutline {...props} ref={ref} />),
    DetailPanel: forwardRef((props, ref) => <ChevronRight {...props} ref={ref} />),
    Edit: forwardRef((props, ref) => <Edit {...props} ref={ref} />),
    Export: forwardRef((props, ref) => <SaveAlt {...props} ref={ref} />),
    Filter: forwardRef((props, ref) => <FilterList {...props} ref={ref} />),
    FirstPage: forwardRef((props, ref) => <FirstPage {...props} ref={ref} />),
    LastPage: forwardRef((props, ref) => <LastPage {...props} ref={ref} />),
    NextPage: forwardRef((props, ref) => <ChevronRight {...props} ref={ref} />),
    PreviousPage: forwardRef((props, ref) => <ChevronLeft {...props} ref={ref} />),
    ResetSearch: forwardRef((props, ref) => <Clear {...props} ref={ref} />),
    Search: forwardRef((props, ref) => <Search {...props} ref={ref} />),
    SortArrow: forwardRef((props, ref) => <ArrowDownward {...props} ref={ref} />),
    ThirdStateCheck: forwardRef((props, ref) => <Remove {...props} ref={ref} />),
    ViewColumn: forwardRef((props, ref) => <ViewColumn {...props} ref={ref} />)
  };
   
    const styles = theme => ({
        paper: {
            
          marginTop:window.innerHeight*0.2,
          marginLeft:(window.innerWidth<600)?window.innerWidth*0.02:window.innerWidth*0.1,
          marginRight:(window.innerWidth<600)?window.innerWidth*0.02:window.innerWidth*0.1,
         // Width: 'auto',
          margin:'auto',
        
         // marginBottom:20,
          maxWidth:600,
        },
        root: {
            maxWidth: 345,
          },
          media: {
            height: 140,
          },
    })
export class CounterOfTeam extends Component {
    state = {
		date: new Date()
	}
    render() {
        return (
  <div style={{  display:'flex',
  flexDirection:'column',
  flexWrap:"wrap",
  justifyContent:"center",
  direction:"rtl"}}>
              

           
      <Card className={this.props.classes.paper}>
            <CardActionArea>
            <PeopleRoundedIcon style={{width:200,height:200,paddingRight:window.innerHeight*0.1}} ></PeopleRoundedIcon>

              <CardContent>
                {/* <Typography gutterBottom variant="h5" component="h2">
                  תאריך
                </Typography> */}
                <Typography style={{paddingBottom:window.innerHeight*0.06,paddingRight:window.innerWidth*0.02,paddingTop:10,color:"#ff9933",}}  variant="h5">
                  6 אנשים באים לתפילה
              </Typography>
              </CardContent>
            </CardActionArea>
          </Card>
      <Typography style={{paddingBottom:window.innerHeight*0.06,paddingRight:window.innerWidth*0.02,paddingTop:10,color:"#ff9933",}}  variant="h5">
            רשימת האנשים שמגעים לתפילה:
              </Typography>

              <div className={this.props.classes.paper}> 
             <MaterialTable
            
            title="  "
            icons={tableIcons}
            columns={[
                {
                    title: 'תפקיד', field: 'name',
                    cellStyle: {
                    //   backgroundColor: '#039be5',
                    //   color: '#FFF',
               
                     align:"right",
                     marginLeft:15,
                   
                    },
                    headerStyle: {
                    //   backgroundColor: '#039be5',
                    marginLeft:15,
                    }
                  },
                  {
                    title: 'שם', field: 'surname',
                    cellStyle: {
                    //   backgroundColor: '#039be5',
                    //   color: '#FFF',
                    direction: 'rtl',
                    align:"right"
                    },
                    headerStyle: {
                    //   backgroundColor: '#039be5',
                    }
                  },
           
            ]}
            data={[
              { name: 'גבאי בית הכנסת', surname: 'דוד בוכריס' },
              { name: 'קורא בתורה', surname: 'אליהו נורי' },
              { name: 'חזן', surname: 'יוסי' }, 
            ]}        
            options={{
              exportButton: true,
            //   exportCsv: (columns, data) => {
            //     alert('You should develop a code to export ' + data.length + ' rows');
            //   }
            }}
          
          />

            </div>

 </div>
        )
    }
}

export default withStyles(styles)(CounterOfTeam);
