import React from 'react';
import { Zoom } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css'
import slide_2 from './images/slide_2.jpg';
import slide_3 from './images/slide_3.jpg';
import slide_4 from './images/slide_4.jpg';
import { withStyles,Card,Typography,Accordion,
  AccordionSummary ,AccordionDetails,Paper  } from '@material-ui/core';
  import Divider from '@material-ui/core/Divider';
const Slideshow = () => {
  const images = [
    slide_2,
    slide_3,
    slide_4,
    slide_2,
    slide_3
  ];
  
  const zoomInProperties = {
    indicators: true,
    scale: 1.4,
   
  }
  return (
  <div>
    <div style={{
      paddingTop:80, 
       Width:window.innerWidth,
      //hight:window.innerhight,
      // backgroundImage: `url(  ${ images[0] }  )` ,
     // marginTop:theme.spacing(),
      width:'auto',
      zIndex:-1,
      maxHight:window.innerhight,
      background: `linear-gradient(#0f0c29, #24243e)`
      //   //backgroundPosition: 'center',
      //    backgroundSize:"100% 95%",
      //   backgroundRepeat: 'no-repeat',
      }}>
      <Typography style={{
        direction:"rtl",
        //paddingTop:30,
        //mxwidth:500,
        paddingRight:window.innerWidth*0.02,
      // color:"#fff"
        }} 
        color="primary"
        variant="h5"
        >
        בית הכנסת אהבת ישראל
      </Typography>
      <Divider style={{paddingTop:5,width: window.innerWidth}}/>
      <div style={{paddingTop:10,}}>
      
        <Zoom {...zoomInProperties} transitionDuration={500} style={{width: window.innerWidth,height:280}}>
          {images.map((each, index) => 
          {return (
            <div key={index} style={{width: '100%'}}>
              <img style={{ objectFit: "cover", width: '100%',height:280 }} src={each}/>
            </div>
          )})}
        </Zoom>
      </div>

      <Divider style={{paddingTop:15,width: window.innerWidth}}/>
      <Typography style={{direction:"rtl",paddingTop:30,paddingRight:window.innerWidth*0.02,}} color="primary" variant="h5">
        רקע כללי
      </Typography>

      <Typography style={{direction:"rtl",paddingRight:window.innerWidth*0.02,paddingTop:10}} color="primary" variant="h5">
        בית הכנסת "אהבת ישראל"
      </Typography>

      <Typography style={{direction:"rtl",paddingRight:window.innerWidth*0.02,paddingLeft:window.innerWidth*0.02,paddingTop:10,color:"#fff",paddingBottom:30}}  variant="h5">
      במזרח גוש עציון למרגלות ההרדיון, על ספר מדבר יהודה, נמצא הישוב הפסטורלי נוקדים. 
      הישוב הוקם בשנת 1982 ומונה כיום כ-300 משפחות המנהלות אורח חיים קהילתי מגוון: 
      המעניק תחושת אחדות הבנה וכבוד הדדי בין אופיין הייחודי של המשפחות.
      </Typography>

 

    </div>
    <Paper>
   

    <Typography style={{direction:"rtl",paddingRight:window.innerWidth*0.02,paddingTop:10}}  variant="h5">
    אי אפשר למצא מעמד מבוסס לרוח כי אם באויר האלהי. הידיעה, ההרגשה, הדמיון והחפץ והתנועות הפנימיות והחיצוניות שלהם, כולם מזקיקים את בני האדם שיהיו אלהיים דוקא. אז ימצאו את מלואם, את יחושם השוה והמניח את הדעת. אם מעט פחות מגדולה זו יבקש לו האדם הרי הוא מיד טרוף כספינה המטורפת בים, גלים סוערים מתנגדים זה לזה ידריכוהו תמיד מנוחה, מגל אל גל יוטל ולא ידע שלו. אם יוכל לשקע באיזה רפש עבה של גסות הרוח ועביות ההרגשה, יצלח לו למעט את אור חייו לאיזה משך זמן, עד שבקרבו ידמה שכבר מצא מנוח. אבל לא יארכו הימים, הרוח יחלץ ממסגרותיו והטירוף הקלעי[2] יחל את פעלו בכל תוקף.
    </Typography> 

    <Typography style={{direction:"rtl",paddingRight:window.innerWidth*0.02,paddingTop:10}}  variant="h5">
    מקום מנוחתנו הוא רק באלקים.
    </Typography>

    <Typography style={{direction:"rtl",paddingRight:window.innerWidth*0.02,paddingTop:10}}  variant="h5">
    אבל האלהים הלא למעלה מכל המציאות אשר יוכל להכנס בקרבנו ממנו איזה רגש ורעיון הוא, וכל מה שהוא למעלה מכל רגש ורעיון בנו הוא לערכנו אין ואפס ובאין ואפס לא תוכל הדעת לנוח. על כן ימצאו על פי רוב תלמידי חכמים מבקשי אלהים יגעים ועיפים ברוח. כשהנשמה הומה לאור היותר בהיר אינה מסתפקת באותו האור הנמצא מהצדק גם במעשים היותר טובים, לא באותו האור הנמצא מהאמת אפילו בלמודים היותר ברורים, ולא בהיופי - אפילו בחזיונות היותר מפוארים, אז מתנול העולם בעיניה: היא כל כך מתרחבת בקרבה, עד שהעולם כולו עם כל גשמיותו ורוחניותו גם יחד, עם כל גילוייו החמריים והרוחניים, נדמה לה לבי עקתא ואוירו נעשה לה מחנק. הם מבקשים מה שהוא למעלה מכחם, מה שהוא לעומתם אין, ולהפך אין ליש אין יכולת גם ברצון לרצות, על כן יחלש לפעמים כח הרצון וכל עז החיים באנשים אשר דרישת אלהים היא מגמתם הפנימית.
    </Typography>

    <Typography style={{direction:"rtl",paddingRight:window.innerWidth*0.02,paddingTop:10}}  variant="h5">
    צריך להראות את הדרך איך נכנסים אל הטרקלין - דרך השער. השער הוא האלהות המתגלה בעולם, בעולם בכל יפיו והדרו, בכל רוח ונשמה, בכל חי ורמש, בכל צמח ופרח, בכל גוי וממלכה, בים וגליו, בשפרירי שחק ובהדרת המאורות, בכשרונות כל שיח, ברעיונות כל סופר, בדמיונות כל משורר ובהגיונות כל חושב, בהרגשת כל מרגיש ובסערת גבורה של כל גבור.
        </Typography>

   
   

    </Paper>
  </div>
  )
}

export default Slideshow;
