import React, { useState, useEffect } from "react";
import {
  withGoogleMap,
  withScriptjs,
  GoogleMap,
  Marker,
  InfoWindow
} from "react-google-maps";

//import mapStyles from "./mapStyles";

function MyMap() {
  const [selectedPark, setSelectedPark] = useState(null);

  useEffect(() => {
    const listener = e => {
      if (e.key === "Escape") {
        setSelectedPark(null);
      }
    };
    window.addEventListener("keydown", listener);

    return () => {
      window.removeEventListener("keydown", listener);
    };
  }, []);

  return (
    <GoogleMap
      defaultZoom={15}
      defaultCenter={{ lat: 31.64533, lng: 35.244064 }}
      //defaultOptions={{ styles: mapStyles }}
    >

    </GoogleMap>
  );
}

const MapWrapped = withScriptjs(withGoogleMap(MyMap));


// Geocode.setApiKey("AIzaSyALVjLwOIM1gf7EzdJJVmWLKdLP-yZGTcw");
// Geocode.enableDebug();

class Map extends React.Component {
 
 state={  
     markerPosition: {
    lat: 31.6333308,
    lng: 35.2166658
},
mapPosition: {
    
    lat: 31.6333308,
    lng: 35.2166658
},}
 

    render() {
      

        return (
            <div style={{ width: "100vw", height: "100vh" }}>
      <MapWrapped
        googleMapURL={`https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=AIzaSyDc9inR_KcwPaO4UTC4TjTIGsmK3KlMV1s`}
        loadingElement={<div style={{ height: `100%` }} />}
        containerElement={<div style={{ height: `100%` }} />}
        mapElement={<div style={{ height: `100%` }} />}
      />
        <Marker
                            //google={this.props.google}
                            name={'Dolores park'}
                            draggable={true}
                            //onDragEnd={this.onMarkerDragEnd}
                            position={{ lat: this.state.markerPosition.lat, lng: this.state.markerPosition.lng }}
                        />
    </div>
        )
    }

}

export default Map;
