import React from 'react';
import { Zoom } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css'
import slide_2 from './images/slide_2.jpg';
import slide_3 from './images/slide_3.jpg';
import slide_4 from './images/slide_4.jpg';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
//import mainLogo from'./logoWhite.png';
const Slideshow = () => {
  const images = [
    slide_2,
    slide_3,
    slide_4,
    slide_3,
    slide_2
  ];
  
  const zoomInProperties = {
    indicators: true,
    scale: 1.4,
   
  }
  return (
    <div style={{
      Width:window.innerWidth,
      //hight:window.innerhight,
      // backgroundImage: `url(  ${ images[0] }  )` ,
      marginTop:0,
      width:'auto',
      zIndex:-1,
      maxHight:window.innerhight,
      background: `linear-gradient(#0f0c29, #24243e)`
      //   //backgroundPosition: 'center',
      //    backgroundSize:"100% 95%",
      //   backgroundRepeat: 'no-repeat',
    }}>
      <Typography style={{
        direction:"rtl",
        paddingTop:80,
        mxwidth:500,
        paddingRight:window.innerWidth*0.02,
        color:"#fff"
        }} 
       
        variant="h5"
        >
        בית הכנסת אהבת ישראל
      </Typography>
      <Divider style={{ color:"#fff"}}></Divider>
      {<Typography
      style={{
        direction:"rtl",
        paddingTop:5,
        mxwidth:500,
        paddingRight:window.innerWidth*0.02,
        color:"#fff"
        }}
        color="primary"
        variant="h5"
      >
        ליוצאי עדות המזרח
      </Typography>}
      <img src={"http://s.cdpn.io/3/kiwi.svg"}/>
      <Zoom {...zoomInProperties} transitionDuration={1000} 
      style={{
        paddingTop:10,
        mxwidth:500,
        width: (window.innerWidth>800)?window.innerWidth*0.75:window.innerWidth,
        height:window.innerHeight*0.8,
        marginLeft:(window.innerWidth>800)?window.innerWidth*0.25:0,
        }}>
        {images.map((each, index) => 
        {return (
          <div key={index} style={{width: '100%',maxwidth:500}}>
            <img style={{ objectFit: "cover", width: '100%',height:window.innerHeight*0.5,maxwidth:500 }} src={each}/>
          </div>
        )})}
      </Zoom>
    </div>
  )
}

export default Slideshow;
