import React from 'react';
import { Zoom } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css'
import slide_2 from './images/slide_2.jpg';
import slide_3 from './images/slide_3.jpg';
import slide_4 from './images/slide_4.jpg';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
//import mainLogo from'./logoWhite.png';
import { ReactComponent as YourSvg } from './svg/torah.svg';
const Slideshow = () => {
  const images = [
    slide_2,
    slide_3,
    slide_4,
    slide_3,
    slide_2
  ];
  
  const zoomInProperties = {
    indicators: true,
    scale: 1.4,
   
  }
  return (
    <div style={{
      Width:window.innerWidth*0.8,
      direction:"rtl",
      //hight:window.innerhight,
      // backgroundImage: `url(  ${ images[0] }  )` ,
      marginTop:0,
      width:'auto',
      zIndex:-1,
      //maxHight:window.innerhight,
      background: `linear-gradient(#0f0c29, #24243e)`
      //   //backgroundPosition: 'center',
      //    backgroundSize:"100% 95%",
      //   backgroundRepeat: 'no-repeat',
    }}>
      
            <Typography style={{
              // direction:"rtl",
              paddingTop:80,
            
              paddingRight:window.innerWidth*0.02,
              color:"#fff"
              }} 
            
              variant="h5"
              >
              בית הכנסת אהבת ישראל

            </Typography>
  
      <Zoom {...zoomInProperties} transitionDuration={1000} 
      style={{
        paddingTop:10,
        maxWidth:window.innerWidth*0.9,
        width: (window.innerWidth>800)?window.innerWidth*0.75:window.innerWidth*0.9,
        height:window.innerHeight*0.8,
        marginLeft:(window.innerWidth>800)?window.innerWidth*0.25:0,
        }}>
        {images.map((each, index) => 
        {return (
          <div key={index} style={{width: '100%',maxwidth:window.innerWidth*0.9}}>
            <img style={{ objectFit: "cover", width: window.innerWidth*0.9,height:window.innerHeight*0.5,maxwidth:500 }} src={each}/>
          </div>
        )})}
      </Zoom>
    </div>
  )
}

export default Slideshow;
